mapper.select:
select
id, uid, type, value, mark, enable, del, create_time, update_time
from initializr_account.admin_contact


    and id = #{id}


    and uid = #{uid}


    and type = #{type}


    and value = #{value}


    and mark = #{mark}


    and enable = #{enable}


    and del = #{del}


    and create_time = #{createTime}


    and update_time = #{updateTime}
